package test

import (
	"testing"
	"encoding/hex"
	"github.com/stretchr/testify/require"
	"github.com/thrift-iterator/go"
	"fmt"
)

func Test_decode_real_struct(t *testing.T) {
	should := require.New(t)
	input, err := hex.DecodeString("")
	should.NoError(err)
	iter := thrifter.NewIterator(input)
	obj := iter.ReadStruct()
	fmt.Println(obj)
	should.NotNil(obj)
}